using System;using System.Collections;using System.Collections.Generic;using UnityEngine;public class TransitionController : MonoBehaviour{  public GameObject[] doors;    public float doorOpenSpeed;  private float _doorDisplacement;  public GameController gameManager;  public bool start;  private void Start()  {    doors = GameObject.FindGameObjectsWithTag("Doors");  }  public void TransitionLevel(int newLevelNumber)  {    //Debug.Log("Start1:" + start);    if (start)    {     // Debug.Log("T");      gameManager.currentLevel++;      start = false;    }    //Debug.Log("Start2:" + start);    //Debug.Log("T");    for (int i = 0; i < doors.Length; i++)    {            DoorAtributes door = doors[i].GetComponent<DoorAtributes>();      if (door.doorLevel == newLevelNumber - 1)      {        if (door.leftDoor)        {          door.transform.position += (Vector3.left * doorOpenSpeed * Time.deltaTime);          _doorDisplacement += (doorOpenSpeed * Time.deltaTime);                 }        else if (!door.leftDoor)        {          door.transform.position += (Vector3.right * doorOpenSpeed * Time.deltaTime);        }        if (_doorDisplacement >= 20.36 * 2)        {           _doorDisplacement = 0;           gameManager.transitionLevel = false;        }      }    }  }}